```cmd
@echo off
setlocal EnableExtensions EnableDelayedExpansion
chcp 65001 >nul

set "QQPath=D:\Program Files (x86)\trueQQ\Bin\QQ.exe"

set "BASE_DIR=%~dp0"
if "%BASE_DIR:~-1%"=="\" set "BASE_DIR=%BASE_DIR:~0,-1%"

set "NAPCAT_PATCH_PACKAGE=%BASE_DIR%\qqnt.json"
set "NAPCAT_LOAD_PATH=%BASE_DIR%\loadNapCat.js"
set "NAPCAT_INJECT_PATH=%BASE_DIR%\NapCatWinBootHook.dll"
set "NAPCAT_LAUNCHER_PATH=%BASE_DIR%\NapCatWinBootMain.exe"
set "NAPCAT_MAIN_PATH=%BASE_DIR%\napcat.mjs"

if not exist "%NAPCAT_LAUNCHER_PATH%" (
  echo NapCat launcher not found: "%NAPCAT_LAUNCHER_PATH%"
  pause
  exit /b 1
)

if not exist "%NAPCAT_INJECT_PATH%" (
  echo NapCat hook dll not found: "%NAPCAT_INJECT_PATH%"
  pause
  exit /b 1
)

if not defined QQPath (
  set "RetString="
  for /f "tokens=2*" %%a in ('reg query "HKEY_LOCAL_MACHINE\SOFTWARE\WOW6432Node\Microsoft\Windows\CurrentVersion\Uninstall\QQ" /v "UninstallString" 2^>nul') do (
    set "RetString=%%~b"
    goto napcat_boot
  )
  for /f "tokens=2*" %%a in ('reg query "HKEY_LOCAL_MACHINE\SOFTWARE\Microsoft\Windows\CurrentVersion\Uninstall\QQ" /v "UninstallString" 2^>nul') do (
    set "RetString=%%~b"
    goto napcat_boot
  )
  echo QQ uninstall registry key not found. Please set QQPath manually.
  pause
  exit /b 1
)

:napcat_boot
if defined RetString (
  for %%a in ("%RetString%") do set "pathWithoutUninstall=%%~dpa"
  set "QQPath=%pathWithoutUninstall%QQ.exe"
)

if not exist "%QQPath%" (
  echo provided QQ path is invalid: "%QQPath%"
  pause
  exit /b 1
)

set "NAPCAT_MAIN_PATH=%NAPCAT_MAIN_PATH:\=/%"
echo (async () =^> {await import("file:///%NAPCAT_MAIN_PATH%")})() > "%NAPCAT_LOAD_PATH%"

if "%~1"=="" (
  "%NAPCAT_LAUNCHER_PATH%" "%QQPath%" "%NAPCAT_INJECT_PATH%"
) else (
  "%NAPCAT_LAUNCHER_PATH%" "%QQPath%" "%NAPCAT_INJECT_PATH%" %1
)

pause
```

